import React, { View } from "react";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import Button from "@material-ui/core/Button";

import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";

const MAX_CYCLE = 5;

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData("1", ""),
  createData("2", ""),
  createData("3", ""),
  createData("4", ""),
  createData("5", "")
];

var operation1 = ["o1", "o2"];

const useStyles = makeStyles(theme => ({
  button: {
    display: "block",
    marginTop: theme.spacing(2)
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 100,
    textAlign: "center"
  },
  table: {
    minWidth: 650
  }
}));

export default function ControlledOpenSelect() {
  const classes = useStyles();
  const [item, setItem] = React.useState("");
  const [operation, setOperation] = React.useState("");

  const [openItem, setOpenItem] = React.useState(false);
  const [openOperation, setOpenOperation] = React.useState(false);

  const [openTimer, setOpenTimer] = React.useState("hidden");
  var myVar;
  var startTimer = false;

  var times = [];

  const handleItemChange = event => {
    // setItem(event.target.value);
    setItem(event.target.value);
  };

  const handleOperationChange = event => {
    setOperation(event.target.value);

    if (item != "" && event.target.value != "") {
      setOpenTimer("visible");
    }
  };

  const handleItemClose = () => {
    setOpenItem(false);
  };

  const handleOperationClose = () => {
    setOpenOperation(false);
  };

  const handleItemOpen = () => {
    setOpenItem(true);
  };

  const handleOperationOpen = () => {
    setOpenOperation(true);
  };

  const showBtn = () => {
    setOpenTimer("visible");
  };

  const timerOnClick = () => {
    if (times.length >= MAX_CYCLE) {
      return 0; // do nothing
    }

    if (startTimer == false) {
      // start timer
      startTimer = true;
      document.getElementById("timerLabel").innerHTML = "Stop";
      myVar = setInterval(myTimer, 1000);
    } else {
      // stop timer
      startTimer = false;
      clearInterval(myVar);
      let t = document.getElementById("demo").innerHTML;
      times.push(t);
      document.getElementById("time_" + (times.length - 1)).innerHTML = t;

      document.getElementById("demo").innerHTML = 0;
      document.getElementById("timerLabel").innerHTML = "Start";

      console.log(times);
    }
  };

  const myTimer = () => {
    var t = document.getElementById("demo").innerHTML - 0;
    document.getElementById("demo").innerHTML = t + 1;
  };

  var itemList = [
    {
      id: 0,
      name: "Shoes 1"
    },
    {
      id: 1,
      name: "Shoes 2"
    }
  ];

  var operationList = [
    {
      item_id: 0,      
      name: [
        {id: 0, task: "chuẩn bị + phối đồ"}, 
        {id: 1, task: "ép góc nóng lạnh + ép mũi"}
      ]
    }
  ];

  return (
    <div>
      <Button className={classes.button} onClick={handleItemOpen}>
        Items here
      </Button>
      <FormControl className={classes.formControl}>
        <InputLabel id="demo-controlled-open-select-label">Item</InputLabel>
        <Select
          labelId="demo-controlled-open-select-label"
          id="demo-controlled-open-select"
          open={openItem}
          onClose={handleItemClose}
          onOpen={handleItemOpen}
          value={item}
          onChange={handleItemChange}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          {/* <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem> */}
          {itemList.map(itemx => (
            <MenuItem value={itemx.id}>{itemx.name}</MenuItem>
          ))}
        </Select>
      </FormControl>

      <Button className={classes.button} onClick={handleOperationOpen}>
        Operations here
      </Button>
      <FormControl className={classes.formControl}>
        <InputLabel id="demo-controlled-open-select-label">
          Operation
        </InputLabel>
        <Select
          labelId="demo-controlled-open-select-label"
          id="demo-controlled-open-select"
          open={openOperation}
          onClose={handleOperationClose}
          onOpen={handleOperationOpen}
          value={operation}
          onChange={handleOperationChange}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          {/* <MenuItem value={50}>Ten</MenuItem>
          <MenuItem value={60}>Twenty</MenuItem>
          <MenuItem value={70}>Thirty</MenuItem> */}
          console.log(item);
          {

            operationList[0]['name'].map(itemx => (              
              <MenuItem value={itemx.id}>{itemx.task}</MenuItem>
            ))
          }


        </Select>
      </FormControl>

      <br />

      <Button
        style={{ visibility: openTimer }}
        onClick={timerOnClick}
        id="timerLabel"
      >
        Start
      </Button>

      <p id="demo"></p>

      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Cycle</TableCell>
              <TableCell align="center">Time</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row, index) => (
              <TableRow key={row.name}>
                <TableCell component="th" scope="row">
                  {row.name}
                </TableCell>
                <TableCell align="center" id={"time_" + index}></TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
